    private void txTituloKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txTituloKeyTyped
        int key = evt.getKeyChar();
        boolean error = false;
        boolean mayusculas = key>=65 && key<=90;
        boolean minusculas = key>=97 && key<=122;
        boolean espacio = key==32;
        if(error== true || !(minusculas || mayusculas || espacio)){
            error=true;
            evt.consume();
        }else error = false; 
    }//GEN-LAST:event_txTituloKeyTyped

    private void txDescripcionKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txDescripcionKeyTyped
        int key = evt.getKeyChar();
        boolean error = false;
        boolean mayusculas = key>=65 && key<=90;
        boolean minusculas = key>=97 && key<=122;
        boolean numero = key>=48 && key<=57;
        boolean especiales = key>=34 && key<=47;
        boolean espacio = key==32;
        if(error== true || !(minusculas || mayusculas || espacio || especiales || numero)){
            error=true;
            evt.consume();
        }else error = false;
    }//GEN-LAST:event_txDescripcionKeyTyped

    private void txPresupuestoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txPresupuestoKeyTyped
        int key = evt.getKeyChar();
        boolean numero = key>=48 && key<=57;
        boolean coma = key==44;
        boolean punto = key==46;
        boolean error = false;
        if(error==true || !(numero || punto || coma)){
            error=true;
            evt.consume();
        }else error = false;
    }//GEN-LAST:event_txPresupuestoKeyTyped

