        ArrayList<Cientifico> cientifico = new ArrayList();
        int pos = tablaMostrarBiologico.rowAtPoint(evt.getPoint());
        int p = (int)tablaMostrarBiologico.getValueAt(pos, 0);
        ExperimentoBiologico biologicoAux = new ExperimentoBiologico();
        biologicoAux = (ExperimentoBiologico) experimentoArray.get(p);
