    private void txTituloFisicoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txTituloFisicoKeyTyped
        int key = evt.getKeyChar();
        boolean error = false;
        boolean mayusculas = key>=65 && key<=90;
        boolean minusculas = key>=97 && key<=122;
        boolean espacio = key==32;
        if(error== true || !(minusculas || mayusculas || espacio)){
            error=true;
            evt.consume();
        }else error = false;
    }//GEN-LAST:event_txTituloFisicoKeyTyped

    private void txDescripcionFisicoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txDescripcionFisicoKeyTyped
        int key = evt.getKeyChar();
        boolean error = false;
        boolean mayusculas = key>=65 && key<=90;
        boolean minusculas = key>=97 && key<=122;
        boolean numero = key>=48 && key<=57;
        boolean especiales = key>=34 && key<=47;
        boolean espacio = key==32;
        if(error== true || !(minusculas || mayusculas || espacio || especiales || numero)){
            error=true;
            evt.consume();
        }else error = false;
    }//GEN-LAST:event_txDescripcionFisicoKeyTyped

    private void txPresupuestoFisicoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txPresupuestoFisicoKeyTyped
        int key = evt.getKeyChar();
        boolean numero = key>=48 && key<=57;
        boolean coma = key==44;
        boolean punto = key==46;
        boolean error = false;
        if(error==true || !(numero || punto || coma)){
            error=true;
            evt.consume();
        }else error = false;
    }//GEN-LAST:event_txPresupuestoFisicoKeyTyped

